def pattern(n):
    out = ''
    if n < 1:
        return out
    change = lambda x: str(x)[-1]
    change2 = lambda x: str(x)[-1]
    for i in range(1, n):
        out += (change(i) * n).center(3*n-2) +'\n'

    for i in range(n):
        out += ''.join(change2(i) for i in range(1,n)+[n]*n+range(n-1,0,-1)) +'\n'

    for i in range(1, n):
        out += (change(n-i) * n).center(3*n-2)  +'\n'
    return out.rstrip('\n')


